import React from 'react';
import './App.css';

var name =['папка1','1папка2','2папка1.1','3папка2.1','4папка2.2','5папка1.1.1'];

var listDir=[
  {nameDir: 'папка1',    id: 0,childe: [2]},
  {nameDir: 'папка2',     id: 1,childe: [3,4]},
  {nameDir: 'папка1.1',   id: 2,childe: [6]},
  {nameDir: 'папка2.1',   id: 3,childe: []},
  {nameDir: 'папка2.2',   id: 4,childe: []},
  {nameDir: 'папка1.1.1', id: 5,childe: []},

];

const App = () =>{

let newList =  name.map( name => <li>{name}</li>);
  return (
    <div className="App">
      <ul><p>список</p>
      {newList}
      </ul>
      <ul><p>список</p>
      {showList(listDir)}
      </ul>
    </div>
  );
}
function showList(list) {

  let newDir =  list.map( list => {
    console.log(list.childe.length);
  if (list.childe.length>0) return (<li>{list.nameDir}</li>+"<ul>");
  else return <li>{list.nameDir}</li>+'</ul>';
  });
  return newDir;
}


export default App;
