import './App.css';
import React from 'react';

const listDir =[
{name: 'папка1', id: 0, parent: null },
{name: 'папка1.1', id: 2, parent: 0, },
{name: 'папка1.1.1', id: 5, parent: 2, },
{name: 'папка2', id: 1, parent: null,},
{name: 'папка2.2', id: 3, parent: 1,},
{name: 'папка2.1', id: 4, parent: 1 },
];

const RenderFolder = ({item}) => {
  let show=(list)=>{return list;}
const children = listDir.filter(dir => dir.parent === item.id);
return <li onClick={show}>
{item.name}
show({children.length && <ul>
{children.map(child => <RenderFolder item={child} />)}
</ul>};)
</li>;
}

function App() {
return (
<ul className="App">
{listDir.filter(item => item.parent === null).map(item => <RenderFolder item={item} />)}
</ul>
);
}

export default App;
