import React from 'react';
import './App.css';

var name =['папка1','1папка2','2папка1.1','3папка2.1','4папка2.2','5папка1.1.1'];

var listDir=[
  {nameDir: 'папка1',     id: 0,childe: [2]},
  {nameDir: 'папка2',     id: 1,childe: [3,4]},
  {nameDir: 'папка1.1.1', id: 5,childe: []},
  {nameDir: 'папка1.1',   id: 2,childe: [6]},
  {nameDir: 'папка2.1',   id: 4,childe: []},
  {nameDir: 'папка2.2',   id: 3,childe: []}
];

const App = () =>{

let newList =  name.map( name => <li>{name}</li>);
  return (
    <div className="App">
      <ul><p>список</p>
      <li>{showList(listDir)}</li>
      </ul>
    </div>
  );
}


function showList(list) {
let newList =  list.map( name => {
  if(name.childe.length){
     let childelist = name.childe.map(childe=>{
       return <li>{childe}</li>;
     });
       return (<ul><p>{name.nameDir}</p>{childelist}</ul>);
  }
});
  return newList;
}


export default App;
