import React from 'react';
import './App.css';


var listDir=[
  {name: 'папка1',     id: 0, parent: null,childrens:[
    {name: 'папка1.1',   id: 2, parent: 0,   childrens:[
      {name: 'папка1.1.1', id: 5, parent: 2,   childrens:[0]}]}]},
  {name: 'папка2',     id: 1, parent: null,childrens:[
    {name: 'папка2.2',   id: 3, parent: 1,   childrens:[0]},
    {name: 'папка2.1',   id: 4, parent: 1,   childrens:[0]}]},
];

const App = () =>{
console.log (listDir);
let newList =  listDir.map( name  => {
  if (name.parent===null) return <li>{name.name}</li>});
  return (
    <div className="App">
      <ul><p>список</p>
      {newList}
      </ul>
      <ul><p>список</p>
      {show(listDir[0])}
      </ul>
    </div>
  );
}
function show(listDir){
  let list = listDir.map(list =>{
    if(list.childrens.length) return <li>{list.childrens.name}</li>;
    else {
      return 0;
    }
  });
  return list;
}



export default App;
