import React from 'react';
import './App.css';


var listDir=[
  {name: 'папка1',     id: 0, parent: null,childrens:[
    {name: 'папка1.1',   id: 2, parent: 0,   childrens:[
      {name: 'папка1.1.1', id: 5, parent: 2,   childrens:[0]}]}]},
  {name: 'папка2',     id: 1, parent: null,childrens:[
    {name: 'папка2.2',   id: 3, parent: 1,   childrens:[0]},
    {name: 'папка2.1',   id: 4, parent: 1,   childrens:[0]}]},
];

const App = () =>{
let newList =  listDir.map( name  => {
  if (name.parent===null) return <li>{name.name}</li>});
  return (
    <div className="App">
      <ul><p>список</p>
      {newList}
      <ul>
      {showchild(listDir[1])}
      </ul>
      </ul>
      <p>{show(listDir[0])}</p>
    </div>
  );

}
function showchild(list){
  let listchild = [0];
  if(list.childrens.length){
     listchild = list.childrens.map(listch => listch.name )}
  else return 0;
  return listchild;
}
function show(list){
  let p =[];
  console.log(list.name);
  p.push(list.name);
  if (list.childrens.length) p.push(show(list.childrens.name));
  return p;
}

export default App;
