import './App.css';
import React, {useState, useEffectfrom} from 'react';

const listDir =[
{name: 'папка1', id: 0, parent: null },
{name: 'папка1.1', id: 2, parent: 0, },
{name: 'папка1.1.1', id: 5, parent: 2, },
{name: 'папка2', id: 1, parent: null,},
{name: 'папка2.2', id: 3, parent: 1,},
{name: 'папка2.1', id: 4, parent: 1 }
];


export default function App() {

const[openedFolder,setOpenedFolder]=useState([]);

const openFolder = id => {
if(openedFolder.includes(id)){
setOpenedFolder(openedFolder.filter(e => e !== id))
} else {
setOpenedFolder([...openedFolder,id])
}
};

const addDir = (id) => {
  listDir.push({name: 'папка2.1.1', id: 6, parent: 2 });
};

const handleOpen = () => this.setState({ modalOpen: true })

const handleClose = () => this.setState({ modalOpen: false })

const RenderFolder = ({item}) => {

const children = listDir.filter(dir => dir.parent === item.id);

return <li>
<button onClick={addDir}>добавить папку</button>

<span onClick={() => children.length && openFolder(item.id)}>{item.name}</span>
{children.length && openedFolder.includes(item.id) && <ul>
{children.map(child => <RenderFolder item={child} />)}
</ul>}
</li>;
};

return (<div>
<ul className="App">
{listDir.filter(item => item.parent === null).map(item => <RenderFolder item={item} />)}
</ul>
<div className="Modal"><Modal /></div>
</div>
);

function Modal(){
  let elimList= React.createRef();

  let del= ()=>{
    let namelist =elimList.current.value;
    listDir.push({name: namelist, id: listDir.length+1, parent: 2 });
  }
  return <div ClassName="ModalOverlay">
     <div ClassName="ModalWindow">
         <div ClassName="ModalHead">
            <div className='modalTitle'>Добавление дикертории</div>
         </div>
         <div ClassName="ModalBody">
         <textarea ref ={elimList}></textarea>
         </div>
         <div ClassName="ModalFooter">
         <button onClick={del}>добавить в списов</button>
         <button>отмена</button>
         </div>
     </div>
  </div>;
};
